@app.route('/read',methods=['GET'])
def read():
    conn = sqlite3.connect('../datos.db')
    myCursor = conn.cursor()
    query="SELECT * FROM Tab;"
    myCursor.execute(query)
    result=myCursor.fetchall()
    print(result)
    return jsonify(result)
