#mqtt#
from xmlrpc.client import DateTime
import paho.mqtt.client as mqtt
#sqlite#
import sqlite3
import datetime


conn = sqlite3.connect('datos.db')

#mqtt
def on_connect(client, userdata, flags, rc):
    print("Connected with result code " + str(rc))
    client.subscribe("swbs/feeds/unomas")


#sqlite#
def on_message(client, userdata, msg):
    
    cur = conn.cursor()
    lecturas = str(msg.payload).split(',')
    temperatura = str(lecturas[0]).split('=')[1]
    humedad = str(lecturas[1]).split('=')[1]
    fecha=datetime.datetime.now()

    
    cur.execute('INSERT INTO Tab (Temperatura, Humedad, Fecha) VALUES(?,?,?)', (temperatura, humedad,fecha))
    conn.commit()
    print(msg.topic + " " + str(msg.payload))

#mqtt#
client = mqtt.Client()
client.on_connect = on_connect
client.on_message = on_message

client.connect("io.adafruit.com", 1883, 60)
client.username_pw_set('swbs', 'aio_NyAe97YqYvLcwrEuyu6eB5b07NaO')

client.loop_forever()